mapper.select:
SELECT I.ID, I.CATEGORY_ID, I.NAME, I.PRICE, I.ORIGIN, I.DISCOUNT, I.IS_GREEN AS isGreen, I.THUMBNAIL_IMAGE as thumbnailImage, B.NAME AS brandName, I.QUANTITY FROM ITEM I JOIN BRAND B ON I.BRAND_ID = B.ID
I.CATEGORY_ID = #{categoryId}
ORDER BY I.ID DESC OFFSET #{start} ROWS FETCH NEXT #{size} ROWS ONLY

SELECT I.ID, I.CATEGORY_ID, I.NAME, I.PRICE, I.ORIGIN, I.DISCOUNT, I.IS_GREEN AS isGreen, I.THUMBNAIL_IMAGE as thumbnailImage, B.NAME AS brandName, I.QUANTITY FROM ITEM I JOIN BRAND B ON I.BRAND_ID = B.ID
I.NAME LIKE '%' || #{keyword} || '%'
AND I.CATEGORY_ID = #{categoryId}
AND I.IS_GREEN = #{isGreen}
ORDER BY I.ID DESC OFFSET #{start} ROWS FETCH NEXT #{size} ROWS ONLY

mapper.update:
UPDATE item SET quantity = quantity - CASE id
WHEN #{item.itemId} THEN #{item.quantity}
ELSE 0 END WHERE id IN
#{item.itemId}
AND quantity >= CASE id
WHEN #{item.itemId} THEN #{item.quantity}
ELSE 0 END

UPDATE item
SET quantity = quantity - #{request.quantity}
WHERE id = #{request.itemId}
  AND quantity >= #{request.quantity}

mapper.select:
SELECT id FROM item WHERE id IN
#{id}
ORDER BY id FOR UPDATE
