mapper.select:
SELECT id, provider
FROM member
WHERE email = #{email}

SELECT GENDER, AGE_RANGE
FROM MEMBER
WHERE ID = #{id}

mapper.update:
UPDATE member
nickname = #{request.nickname}, age_range = #{request.ageRange}, gender = #{request.gender}, profile_image = #{request.profileImage},
WHERE id = #{request.id}

mapper.select:
SELECT is_signup_completed
FROM member
WHERE id = #{id}

SELECT COUNT(*) as newMemberCount
FROM member
WHERE created_at >= #{from} AND created_at < #{to}
